import { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { setSubmissionDirty, useUpdateSubmissionMutation } from '../store';

function submissionUploader()
{
    console.log("checking submission upload");
    const user = useSelector(state => state.userData);
    const userQuestions = useSelector((state)=>state.questionsData);
    const [updateSubmission, updateResults] = useUpdateSubmissionMutation();
    const dispatch = useDispatch();


    useEffect(()=>{

        if(user.id == 0) return;
        if(!user.startedExam) return;

        let answers = [];
        const submission = {id: user.id, answers: answers};
        userQuestions.data.map((question) =>
        {
            answers.push({id: question.id, answerId: question.userAnswerId || 0});
        })

        updateSubmission(submission);
        console.log("Updated submission after mutation");
        dispatch(setSubmissionDirty({value: false}));

    }, [userQuestions]);
}

export default submissionUploader;
