import { useDispatch } from 'react-redux';
import { useEffect } from 'react';
import { useState } from 'react';
import { changePath, 
    changeUser, 
    CONFIRM_PAGE_PATH, 
    loadPath, 
    QUESTIONS_PAGE_PATH, 
    setUserField, 
    useFetchUserQuery, 
    WAIT_PAGE_PATH 
} from '../store';
import { currentTimeLeft } from '../Components/ExamTimer';
import useChangePage from '../Hooks/navigationHooks';

function ApplicantLogin()
{
    const [loginError, setLoginError] = useState(false);
    const [loginInput, setLoginInput] = useState('');
    const [loginSubmit, setLoginSubmit] = useState('');
    const dispatch = useDispatch();

    const changePage = useChangePage();

    const {data, error, isLoading} = useFetchUserQuery(loginSubmit);
    const inputStyle = loginError ? {borderColor: `#da4242`} : {borderColor: `#7caaf0`}

    // Updates loginInput to latest user input 
    const handleLoginInputChange = (event)=>
    {
        setLoginInput(event.target.value);
        if(loginError) setLoginError(false);
    }

    // Updates loginSubmit, which triggers useFetchUserQuery again
    const handleSubmit = (event) =>
    {
        event.preventDefault();
        setLoginSubmit(loginInput);
    }

    // Validates user updating userData and loading next page
    const validateLogin = () =>
    {
        const user = data[0];
        dispatch(changeUser(user));
        if(user.submitedExam)
        {
            dispatch(changePath(WAIT_PAGE_PATH));
        }
        else if(user.startedExam)
        {
            if(currentTimeLeft(user) <= 0)
            {
                dispatch(setUserField({key:"submitedExam", value:true}));
                dispatch(changePath(WAIT_PAGE_PATH));
            }
            else
            {
                dispatch(loadPath(QUESTIONS_PAGE_PATH));
            }
        }
        else
        {
            changePage(CONFIRM_PAGE_PATH);
            // dispatch(loadPath(CONFIRM_PAGE_PATH));
        }
    }

    // Handle data coming from data base 
    useEffect(()=>{
        if(data) {
            if(data.length > 0 && !isLoading && error == null)
            {
                validateLogin();
            }
            else if(!isLoading && data.length <= 0 && loginInput.length > 0)
            {
                setLoginError(true);
            }
            else
            {
                //TODO -> display loading
            }
        }
    }, [data]);

    return <div className="centerForm">
        <div className="centerScreen" style={{display: `inline-block`}}>
        <form onSubmit={handleSubmit} className="car-form panel">
            <div className='field-group'>
                <div className='field'>
                    <label className='label'>Email: </label>
                    <input 
                        style={inputStyle}
                        className="input is-xpanded"
                        value={loginInput}
                        onChange={handleLoginInputChange}
                    ></input>
                </div>
            </div>
            <div className="field" style={{paddingTop: 10}}>
                <button style={{fontSize : `16px`}}>Login</button>
            </div>
        </form>
        </div>
    </div>
}

export default ApplicantLogin;
