import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { changeUser } from "../store";
import { 
    useLazyFetchUserQuery
} from "../store";

function TryUserLogin(loginInput)
{
    const dispatch = useDispatch();
    const [loginError, setLoginError] = useState(false);
    const [fetchUser, results] = useLazyFetchUserQuery();
    const user = useSelector(state=>state.userData);

    // Dispatch loaded user data
    useEffect(() => {
        if(loginInput == '') return;

        if(results.isUninitialized) 
        {
            fetchUser(loginInput);
            return;
        }

        if(results.isFetching  ||
            results.isLoading || 
            results.error || 
            user.id != 0) return;

        console.log(results.data[0]);

        // if(results.isSuccess && results.data.length <= 0){
        //     setLoginError(true);
        //     return;
        // }

        // dispatch(changeUser(results.data[0]));
        // console.log(`login validated, user dispatched`);
        // console.log(fetchUser.data[0]);
    });

    return [results.error];
}

export default TryUserLogin;
