import { useSelector } from "react-redux";

function ExamTimer()
{
    const date = new Date();
    const user = useSelector(state => state.userData);
    const startTime = user.id != 0 ? user.examStartTime : "30/01/2023-17:35:04";

    const deltaSeconds = date.getSeconds() - parseInt(startTime.split("-")[1].split(":")[2]);
    const deltaMinutes = date.getMinutes() - parseInt(startTime.split("-")[1].split(":")[1]);
    const deltaHours = date.getHours() - parseInt(startTime.split("-")[1].split(":")[0]);
    const deltaDays =  date.getDate() - parseInt(startTime.split("-")[0].split("/")[0]);
    const timeLeft = `${deltaHours}:${deltaMinutes}:${deltaSeconds}`;

    console.log(deltaDays);

    useEffect(() => {
        timeLeft = `${deltaHours}:${deltaMinutes}:${deltaSeconds}`;
    }, [date.getSeconds()]);

    return <div>{timeLeft}</div>
}

export default ExamTimer;
