import { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { setUserField, useUploadUserMutation, usePostSubmissionMutation } from '../store';

function useUploadUser()
{
    const [uploadUser, updateUserResults] = useUploadUserMutation();
    const user = useSelector((state)=> state.userData);
    const dispatch = useDispatch();

    useEffect(()=>{

        if(user.id == 0) return;
        if(!user.isDirty) return;

        uploadUser(user);
        console.log("Uploaded user after mutation");
        dispatch(setUserField({key: "isDirty", value:false}));

    }, [user]);

    const userQuestions = useSelector((state)=>state.questionsData);
    const [submmitExam, submitResults] = usePostSubmissionMutation();

    useEffect(()=>{
        if(user.id == 0) return;
        if(!user.startedExam) return;

        let answers = [];
        userQuestions.data.map((question) =>
        {
            answers.push({id: question.id, answerId: question.userAnswerId || 0});
        })
        const submission = {userId: user.id, answers: answers};
        submmitExam(submission);
        console.log("Uploaded submission after mutation");
        dispatch(setDirty(false));
    }, [userQuestions]);

}

export default useUploadUser;
