import { useSelector, useDispatch } from 'react-redux';
import { useState } from 'react';
import { changeLoginInput, CONFIRM_PAGE_PATH, loadPath, useFetchUserQuery } from '../store';
import { useEffect } from 'react';

function ApplicantLogin()
{
    const [auth, setAuth] = useState('not-logged');

    const dispatch = useDispatch();
    const loginInput = useSelector((state) =>
    {
        return state.login.loginInput;
    })
    const handleLoginInputChange = (event)=>
    {
        dispatch(changeLoginInput(event.target.value));
        console.log(event.target.value);
        if(event.target.value == "") 
        {
            setAuth('not-logged');
        }
    }

    const {data, error, isLoading} = useFetchUserQuery(loginInput);

    let inputStyle = auth == 'validated '? {style: {borderColor: `#0af00`} }: {style: {borderColor: `#da4242`}};
    inputStyle = auth == 'not-logged' ? {style: {borderColor: `#00000`}} : inputStyle;

    const handleSubmit = (event) =>
    {
        event.preventDefault();
        if(data.length > 0 && !isLoading && error == null)
        {
            if(!isLoading) console.log(data);
            dispatch(loadPath(CONFIRM_PAGE_PATH));
            setAuth('validated');
        }
        else
        {
            console.log("invalid login");
            setAuth('no-user');
            console.log(inputStyle);
        }
    }


    useEffect(() =>
        {
            dispatch(changeLoginInput(""));
    },[])

    return <div className="centerForm">
        <div className="centerScreen" style={{display: `inline-block`}}>
        <form onSubmit={handleSubmit} className="car-form panel">
            <div className='field-group'>
                <div className='field'>
                    <label className='label'>Email: </label>
                    <input 
                        style={inputStyle.style}
                        className="input is-xpanded"
                        value={loginInput}
                        onChange={handleLoginInputChange}
                    ></input>
                </div>
            </div>
            <div className="field" style={{paddingTop: 10}}>
                <button style={{fontSize : `16px`}}>Login</button>
            </div>
        </form>
        </div>
    </div>
}

export default ApplicantLogin;
