import { useState } from "react";
import { useDispatch } from "react-redux";
import { selectAlternative } from "../store";
import { useEffect } from "react";

function Question({data})
{
    const [userAnswer, setUserAnswer] = useState({});
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(selectAlternative({questionId: data.id, alt: userAnswer.id}));
    }, [userAnswer]);

    const alternativesContent = data.alternatives.map((alternative) =>
    {
        const selectionStyle = userAnswer.id == alternative.id ? { fontWeight: 'bold', fontSize: 20 } : { fontSize: 15}
        const content = <div 
            className="box"
            style={selectionStyle}
            onClick={() => setUserAnswer(alternative)} 
            key={alternative.id}>{alternative.answer}
        </div>
        return content;
    });

    return <div className="box" >
        <div style={{paddingLeft: 20}}>(){data.question}</div>
        <div style={{paddingTop: 10}}>
            {alternativesContent}
        </div>

    </div>
}

export default Question;
