import { useState } from "react";
import { useDispatch } from "react-redux";
import { selectAlternative } from "../store";

function Question({data}, {onSelectAlt})
{
    const [answer, setAnswer] = useState({});
    const dispatch = useDispatch();

    const handleAlternativeSelect = (alternative) =>
    {
        console.log(alternative);
        setAnswer(alternative);
        dispatch(selectAlternative({questionId: data.id, alt: alternative.id}));
    }

    const alternativesContent = data.alternatives.map((alternative) =>
    {
        const selectionStyle = answer.id == alternative.id ? { fontWeight: 'bold', fontSize: 20 } : { fontSize: 15}
        return <div 
            className="box"
            style={selectionStyle}
            onClick={() => {handleAlternativeSelect(alternative)}} 
            key={alternative.id}>{alternative.answer}</div>
    });


    return <div className="box" >
        {data.question}
        <div>
            {alternativesContent}
        </div>

    </div>
}

export default Question;
