import Link from '../Components/Link';
import { QUESTIONS_PAGE_PATH, useUpdateSubmissionMutation, WAIT_PAGE_PATH } from '../store';
import { setUserField } from '../store';
import { useSelector, useDispatch } from 'react-redux';
import useChangePage from '../Hooks/useChangePage';
import { setSubmissionDirty } from '../store';
import useSetUserField from '../Hooks/useSetUserField';

function SubmitExam()
{
    const [updateExam, updateResults] = useUpdateSubmissionMutation();

    const examPeriodLeft = 90;
    const submitDetails = `You still have a time period of ${examPeriodLeft} minutes to complete the exam. Are you sure you want to submmit your answers ? Please verify all of them. After this exam submmision you won't be able change or restart the exam.` ;

    const submitContent = "After submission we will review you answers and get in touch with feeback.";

    const userQuestions = useSelector((state)=> state.questionsData);
    const user = useSelector((state)=> state.userData);

    const changePath = useChangePage();
    const setUserField = useSetUserField();

    const handleConfirmSubmit = () =>{
        // updates user
        setUserField("submitedExam", true);

        // TODO => load 'submission complete' page
        changePath(WAIT_PAGE_PATH);
    }

    return <div style={ { padding: 15, paddingLeft: 20 }}>
        <h1 style={{fontSize: 23}}>Confirm exam details :</h1>
        <div style={{ paddingTop: 10}}>
            <div style={{ border: '2px solid color:#282c3451', borderRadius: 20, padding: 5, backgroundColor: '#282c3451' }}>
                <h1 style={{padding: 10, fontSize: 15}}>
                    {submitDetails}
                </h1>
                <h1 style={{ padding: 10, fontSize: 15}}>
                    {submitContent}
                </h1>
                <h1 style={{padding : 10}}>{`Best of luck! HR Team`}</h1>
            </div>
            <div style={{paddingTop: 15, 
                display: "flex", 
                justifyContent: "center", 
                }}>
                <Link to={QUESTIONS_PAGE_PATH}>
                    <button style={{fontSize : `16px`}}>RETURN TO QUESTIONS</button>
                </Link>
                <Link to={WAIT_PAGE_PATH}>
                    <button onClick={handleConfirmSubmit} style={{fontSize : `16px`}}>CONFIRM SUBMIT</button>
                </Link>
            </div>
        </div>
    </div>
}

export default SubmitExam;
