import Link from '../Components/Link';
import { QUESTIONS_PAGE_PATH, useFetchTextQuery } from '../store';

function ConfirmExam()
{
    const {data, error, isLoading} = useFetchTextQuery('confirm_exam');

    let texts = [];
    if(!isLoading)
    {
        texts = data.map(text =>
            {
                return {text};
            });
        }

    console.log(data[0].id);
    const jobHole = "Full Stack Developer";
    const examContent = texts.length > 0 ? texts[0].value : "error loading";


    const examPeriodMinutes = 90;
    const hrMailContact = 'HR@myCompany.com';
    const examDetails = `You will have a time period of ${examPeriodMinutes} minutes to complete the exam after confirming the start. Note that if you leave or reload the page the current answers will be discarted. Other attempts will have to be solicited to ${hrMailContact}. `;

    return <div style={ { padding: 15, paddingLeft: 20 }}>
        <h1 style={{fontSize: 23}}>Confirm exam details :</h1>
        <div style={{ paddingTop: 10}}>
            <div style={{ border: '2px solid color:#282c3451', borderRadius: 20, padding: 5, backgroundColor: '#282c3451' }}>
                <h1 style={{padding: 10, fontSize: 15}}>
                    {examContent}
                </h1>
                <h1 style={{ padding: 10, fontSize: 15}}>
                    {examDetails}
                </h1>
            </div>
            <div style={{paddingTop: 15}}>
                <Link to={QUESTIONS_PAGE_PATH}>
                    <button style={{fontSize : `16px`}}>CONFIRM</button>
                </Link>
            </div>
        </div>
    </div>
}

export default ConfirmExam;
