import { useState } from "react";

function Question(qObject)
{
    const handleAlternativeSelect = (alternative) =>
    {
        setAnswer(alternative);
    }

    const [asnwer, setAnswer] = useState("");
    const alternativesContent = qObject.data.alternatives.map((alternative) =>
    {
        const selectedStyle = asnwer == alternative ? { fontWeight: 'bold' } : {}
        return <div 
            style={selectedStyle}
            onClick={() => handleAlternativeSelect(alternative)} 
            key={alternative}>{alternative}</div>
    });


    return <div className="question box" >
        Title: {qObject.data.question}
        {alternativesContent}
    </div>
}

export default Question;
