import { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { setUserField, useUploadUserMutation } from '../store';

function useUploadUser()
{
    const [uploadUser, updateUserResults] = useUploadUserMutation();
    const user = useSelector((state)=> state.userData);
    const dispatch = useDispatch();

    useEffect(()=>{
        if(user.id == 0 || !user.isDirty) return;
        uploadUser(user);
        dispatch(setUserField({key: "isDisrty", value:false}));
        console.log("Uploaded user after mutation");
    }, [user]);

    return () => {uploadUser(user)}
}

export default useUploadUser;
