function InfoForm()
{
    const {data, error, isLoading} = useFetchTextQuery('confirm_exam');
    const userEmail = useSelector((state) => state.userData.email);

    if(isLoading || error) return;

    const confirmHeader = `${userEmail} welcome to Interview Hub !! \nPlease confirm exam details :`;
    const confirmContent = data.map(text => {
        return <div style={{ padding: 10, fontSize: 15}} key={text.id}>
            {text.value}
        </div>
    })
    return <div style={ { padding: 15, paddingLeft: 20 }}>
        <Text style={{fontSize: 18}}>{confirmHeader}</Text>
        <div style={{ paddingTop: 10}}>
            <div style={{ border: '2px solid color:#282c3451', borderRadius: 20, padding: 5, backgroundColor: '#282c3451' }}>
                {confirmContent}
            </div>
            <div>
                <Link to={QUESTIONS_PAGE_PATH}>
                    <button style={{fontSize : `16px`}}>CONFIRM</button>
                </Link>
            </div>
        </div>
    </div>
}

export default InfoForm;
