import { useDispatch } from 'react-redux';
import { useEffect } from 'react';
import { useState } from 'react';
import { changeUser, CONFIRM_PAGE_PATH, loadPath, useFetchUserQuery } from '../store';

function ApplicantLogin()
{
    const [authError, setAuthError] = useState(false);
    const [loginInput, setLoginInput] = useState('');
    const [loginSubmit, setLoginSubmit] = useState('');

    const dispatch = useDispatch();
    const handleLoginInputChange = (event)=>
    {
        setLoginInput(event.target.value);
    }

    const {data, error, isLoading} = useFetchUserQuery(loginSubmit);

    const inputStyle = authError ? {borderColor: `#da4242`} : {borderColor: `#7caaf0`}

    const handleSubmit = (event) =>
    {
        event.preventDefault();
        setLoginSubmit(loginInput);
    }

    if(data) {
        console.log(data);
        if(data.length > 0 && !isLoading && error == null)
        {
            if(data[0].submitedExam) return;

            console.log("loggin in!");
            dispatch(changeUser(data[0]));
            dispatch(loadPath(CONFIRM_PAGE_PATH));
        }
        else if(!isLoading && (error != null || data.length <= 0))
        {
            // TODO -> display error
            console.log("login error");
            setAuthError(true);
        }
        else
        {
            //TODO -> display loading
        }
    }

    return <div className="centerForm">
        <div className="centerScreen" style={{display: `inline-block`}}>
        <form onSubmit={handleSubmit} className="car-form panel">
            <div className='field-group'>
                <div className='field'>
                    <label className='label'>Email: </label>
                    <input 
                        style={inputStyle}
                        className="input is-xpanded"
                        value={loginInput}
                        onChange={handleLoginInputChange}
                    ></input>
                </div>
            </div>
            <div className="field" style={{paddingTop: 10}}>
                <button style={{fontSize : `16px`}}>Login</button>
            </div>
        </form>
        </div>
    </div>
}

export default ApplicantLogin;
