import { useState } from "react";

function Question({data}, handleSelect)
{
    const [answer, setAnswer] = useState(0);

    const handleAlternativeSelect = (alternative) =>
    {
        setAnswer(alternative.id);
        handleSelect(alternative);
        //console.log(alternative.id);
    }

    const alternativesContent = data.alternatives.map((alternative) =>
    {
        const selectionStyle = answer == alternative.id ? { fontWeight: 'bold', fontSize: 20 } : { fontSize: 15}
        return <div 
            className="box"
            style={selectionStyle}
            onClick={() => handleAlternativeSelect(alternative)} 
            key={alternative.id}>{alternative.answer}</div>
    });


    return <div className="box" >
        {data.question}
        <div>
            {alternativesContent}
        </div>

    </div>
}

export default Question;
