import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { changeUser } from "../store";
import { 
    useFetchUserQuery
} from "../store";

function TryUserLogin(loginInput)
{
    const dispatch = useDispatch();
    const [loginError, setLoginError] = useState(false);
    const [fetchUser, results] = useLazyFetchUserQuery();
    const user = useSelector(state=>state.userData);

    // Dispatch loaded user data
    useEffect(() => {
        if(loginInput == '') return;

        fetchUser(loginInput);

        if(fetchUser.isFetching  ||
        fetchUser.isLoading || 
        fetchUser.error || 
        user.id != 0) return;

        if(fetchUser.isSuccess && fetchUser.data.length <= 0){
            setLoginError(true);
            return;
        }

        dispatch(changeUser(fetchUser.data[0]));
        // console.log(`login validated, user dispatched`);
        // console.log(fetchUser.data[0]);

        return;
    });

    return [fetchUser.isLoading, loginError];
}

export default TryUserLogin;
