import { createSlice } from '@reduxjs/toolkit';

const submissionSlice = createSlice({
    name: 'submissionData',
    initialState:{
        isDirty: true,
        answers: [
            {
                "questionId": 14,
                "answerId": 3
            }
        ],
        id: 0
    },
    reducers:
    {
        setupSubmission({answers, isDirty, id}, action)
        {
            console.log(answers);
            state.isDirty = action.payload.isDirty;
            state.answers = action.payload.answers;
            state.id = action.payload.id;
        },
        selectAlternative(state, action)
        {
            state.isDirty = true;
            state.answers.map((answer) => {
                if(answer.questionId == action.payload.questionId)
                {
                    answer.answerId = action.payload.alt;
                }
            })
        },
        setSubmissionDirty(state, action)
        {
            state.isDirty = action.payload.value;
        }
    }
});

export const { setupSubmission, selectAlternative, setSubmissionDirty } = submissionSlice.actions;
export const submissionReducer = submissionSlice.reducer;
