import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react';

const questionsApi = createApi({
    reducerPath: 'questions',
    baseQuery: fetchBaseQuery({
        baseUrl: 'http://localhost:3005'
    }),
    endpoints(builder){
        return{
            fecthQuestions: builder.query({
                query: (question) => {
                    return{
                        url: '/questions',
                        params: {
                            id: question.id
                        },
                        method: 'GET',
                    };
                }
            })
        };
    }
})

export const { useFecthQuestionQuery } = questionsApi;
export { questionsApi };
