import Link from '../Components/Link';
import Question from '../Components/Question';
import { SUBMIT_PAGE_PATH, useFetchQuestionsQuery, useFetchSubmissionQuery, loadQuestionsData, useUploadUserMutation, setUserField, setupSubmission} from '../store';
import { useSelector, useDispatch } from 'react-redux';
import { useEffect } from 'react';

function QuestionsExam()
{
    const user = useSelector((state)=> state.userData);
    const questionsData = useSelector((state)=> state.questionsData.data);
    const {data, qError, qIsLoading} = useFetchQuestionsQuery();
    const submissionFetch = useFetchSubmissionQuery(user);
    const dispatch = useDispatch();

    let filteredQuestions = [];

    // After fecthing the data, filter user questions and dispatch
    useEffect(() => {
        if(!data)return;

        data.map((question) =>
        {
            user.questionsId.map((id) => {
                if(id == question.id) 
                {
                    filteredQuestions.push(question);
                }
            });
        })

        dispatch(loadQuestionsData(filteredQuestions));

        if(!user.startedExam)
        {
            const newDate = new Date();
            const startTime = `${newDate.getDate()}/${newDate.getMonth()+1}/${newDate.getFullYear()}-${newDate.getHours()}:${newDate.getMinutes()}:${newDate.getSeconds()}`;
            dispatch(setUserField({key:"startedExam", value: true}));
            dispatch(setUserField({key:"examStartTime", value: startTime}));
        }
        return;
    }, [data]);

    useEffect(()=>{
        if(!submissionFetch.data)return;
        if(!user.startedExam)
        {
            // Create submission slice with loaded questions 
            let answers = [];
            const submission = {id: user.id, answers: answers, isDirty: true};
            data.map((question) =>
            {
                answers.push({questionId: question.id, answerId: question.userAnswerId || 0});
            })
            dispatch(setupSubmission(submission));
        }
        else
        {
            dispatch(setupSubmission(submissionFetch.data));
            console.log(submissionFetch.data);
        }
        return;
    }, [submissionFetch.data]);

    // TODO -> display skeleton while loading || display
    if(qIsLoading || qError) return;


    const questionsContent = questionsData.map((qObject) => 
    {
        return <div key={qObject.id} style={{paddingTop: 5}}>
            <div style={{paddingLeft: 15, paddingRight: 15}}>
                <Question data={qObject}/>
            </div>
        </div>
    })

    return <div>
        <h3 style={{display: 'flex',  justifyContent:'center', fontSize: 24}}>Questions:</h3>
        {questionsContent}
        <div style={{display: 'flex', justifyContent:'center',paddingTop: 35, heigth: 40}}>
            <Link to={SUBMIT_PAGE_PATH}>
                <button style={{fontSize : `16px`}}>SUBMIT</button>
            </Link>
            <div style={{paddingTop: 55}}></div>
        </div>
    </div>
}

export default QuestionsExam;
