import { useDispatch } from "react-redux";
import { changeUser } from "../store";

function useLoadUser()
{
    const dispatch = useDispatch();

    // Dispatch user data
    const user = data[0];
    dispatch(changeUser(user));

    // Filter and dispatch loaded user questions
    let filteredQuestions = [];
    useEffect(() => {
        if(!data)return;

        // console.log(data);
        data.map((question) =>
        {
            user.questionsId.map((id) => {
                if(id == question.id) 
                {
                    filteredQuestions.push(question);
                }
            });
        })

        dispatch(loadQuestionsData(filteredQuestions));

        if(!user.startedExam)
        {
            const newDate = new Date();
            const startTime = `${newDate.getDate()}/${newDate.getMonth()+1}/${newDate.getFullYear()}-${newDate.getHours()}:${newDate.getMinutes()}:${newDate.getSeconds()}`;
            dispatch(setUserField({key:"startedExam", value: true}));
            dispatch(setUserField({key:"examStartTime", value: startTime}));
        }
        return;
    }, [data]);    
}

export default useLoadUser;
