import { useDispatch } from "react-redux";
import { changeUser } from "../store";

function useLoadUser(loginInput)
{
    const dispatch = useDispatch();
    const fetchUser = useFetchUserQuery(loginInput);


    // Dispatch loaded submission
    useEffect(()=>{
        if(!submissionFetch.data || 
            submissionFetch.isLoading || 
            submissionFetch.error) return;

        // When user is created, so is the submission
        // so we don't need to check for anything
        dispatch(setupSubmission(submissionFetch.data[0]));
        console.log('loaded submission to slice');

        return;
    }, [submissionFetch.data]);

    // Filter and dispatch loaded user questions
    let filteredQuestions = [];
    useEffect(() => {
        if(!data)return;

        // console.log(data);
        data.map((question) =>
        {
            user.questionsId.map((id) => {
                if(id == question.id) 
                {
                    filteredQuestions.push(question);
                }
            });
        })

        dispatch(loadQuestionsData(filteredQuestions));
        console.log('loaded filtered questions to slice');

        if(!user.startedExam)
        {
            const newDate = new Date();
            const startTime = `${newDate.getDate()}/${newDate.getMonth()+1}/${newDate.getFullYear()}-${newDate.getHours()}:${newDate.getMinutes()}:${newDate.getSeconds()}`;
            dispatch(setUserField({key:"startedExam", value: true}));
            dispatch(setUserField({key:"examStartTime", value: startTime}));
        }

        return;
    }, [data]);    

    // Dispatch loaded user data
    useEffect(() => {
        if(!fetchUser.data || fetchUser.isLoading || fetchUser.error) return;

        dispatch(changeUser(fetchUser.data));
        console.log('loaded user to slice');

        return;
    }, [fetchUser.data]);
}

export default useLoadUser;
