import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react';

const submissionsApi = createApi({
    reducerPath: 'submissions',
    baseQuery: fetchBaseQuery({
        baseUrl: 'http://localhost:3005'
    }),
    endpoints(builder){
        return{
            fetchSubmission: builder.query({
                query: (user) => {
                    return{
                        url: '/submissions',
                        params: {
                            userId: user.id
                        },
                        method: 'GET',
                    };
                }
            }),
            postSubmission: builder.mutation({
                query: (submission) => {
                    return{
                        url: '/submissions',
                        body: {
                            userId: submission.userId,
                            answers: submission.answers
                        },
                        method: 'POST',
                    };
                }
            }),
            updateSubmission: builder.mutation({
                query: (submission) => {
                    return{
                        url: '/submissions',
                        body: {
                            userId: submission.userId,
                            answers: submission.answers
                        },
                        method: 'PATCH',
                    };
                }
            })
        };
    }
})

export const { useFetchSubmissionQuery, usePostSubmissionMutation } = submissionsApi;
export { submissionsApi };
