import { createSlice } from '@reduxjs/toolkit';

const questionsSlice = createSlice({
    name: 'questionsData',
    initialState:{
        data: [
        {
            id: 0,
            question: "",
            alternatives: [{id: 1, answer: "default"}], 
            answerId: 0
        }]
    },
    reducers:
    {
        loadQuestionsData(state, action)
        {
            state.data = action.payload;
        },
        selectAlternative(state, action)
        {
            state.data.map((qObject) => {
                if(qObject.id == action.payload.id)
                {
                    qObject.answerId = action.payload.answer;
                }
            })
        }
    }
});

export const { loadQuestionsData } = questionsSlice.actions;
export const questionsReducer = questionsSlice.reducer;
