import Link from '../Components/Link';
import Question from '../Components/Question';
import { SUBMIT_PAGE_PATH, useFetchQuestionsQuery, loadQuestionsData} from '../store';
import { useSelector, useDispatch } from 'react-redux';
import {answer} from '../Components/Question';

function QuestionsExam()
{
    const user = useSelector((state)=> state.userData);
    const {data, error, isLoading} = useFetchQuestionsQuery();
    const qObjects = data;
    const dispatch = useDispatch();

    // TODO -> display skeleton while loading || display
    if(isLoading || error) return;

    let filteredQuestions = [];
    data.map((question) =>
    {
        user.questionsId.map((id) => {
            if(id == question.id) 
            {
                filteredQuestions.push(question);
                return;
            }
        });
        return;
    })
    const selectAlternative = ({answer}) => {
        console.log(answer);
    }
    const questionsContent = filteredQuestions.map((qObject) => 
    {
        return <div key={qObject.id} style={{paddingTop: 5}}>
            <Question data={qObject} onSelectAlt={() => selectAlternative(qObject)}/>
        </div>
    })

    const handleStartSubmit = () =>{
        dispatch(loadQuestionsData(filteredQuestions));
        console.log(`submiting user questions:`);
        console.log(filteredQuestions);
    }
    //dispatch(loadQuestionsData(filteredQuestions));

    return <div className="centerForm">
        <div className="centerScreen"> 
            <h3>Questions:</h3>
            {questionsContent}
            <div style={{paddingTop: 35}}>
                <Link to={SUBMIT_PAGE_PATH}>
                    <button onClick={handleStartSubmit} style={{fontSize : `16px`}}>SUBMIT</button>
                </Link>
            </div>
        </div>
    </div>
}

export default QuestionsExam;
