import { useState } from "react";

function Question(qObject)
{
    const [answer, setAnswer] = useState(0);

    const handleAlternativeSelect = (alternative) =>
    {
        console.log(alternative.id);
        setAnswer(alternative.id);
        console.log(answer.id);
    }

    const alternativesContent = qObject.data.alternatives.map((alternative) =>
    {
        //console.log(`${answer.id} == ${ alternative.id} : ${answer.id == alternative.id}`);
        const selectionStyle = answer.id == alternative.id ? { fontWeight: 'bold', fontSize: 20 } : { fontSize: 15}
        return <div 
            className="box"
            style={selectionStyle}
            onClick={() => handleAlternativeSelect(alternative)} 
            key={alternative.id}>{alternative.answer}</div>
    });


    return <div className="box" >
        Title: {qObject.data.question}
        {alternativesContent}
    </div>
}

export default Question;
