import './App.css';
import Link from './Components/Link';
import ApplicantLogin from './Pages/ApplicantLogin';
import ConfirmExam from './Pages/ConfirmExam';
import Route from './Components/Route';
import { useDispatch, useSelector } from 'react-redux';
import { previousPage, loadPath, changePath, LOGIN_PAGE_PATH, CONFIRM_PAGE_PATH, QUESTIONS_PAGE_PATH } from './store';
import { useEffect } from 'react';

function App() {

  const dispatch = useDispatch();
  useEffect(() =>
  {
    Start();
  }, []);

  const previousPage = useSelector((state)=>
    {
      return state.navigation.currentPath
    }
  );

  const Start = ()=>
  {
    dispatch(loadPath(LOGIN_PAGE_PATH));

    const handler = (event) =>
    {
      console.log(previousPage);
      if(previousPage != LOGIN_PAGE_PATH)
      {
        dispatch(changePath(window.location.pathname));
      }
    }
    window.addEventListener('popstate', handler);
    return () =>
    {
        window.removeEventListener('popstate', handler)
    }
  }

  return (
    <div className="">
      <header className="App-header">
        <h1>Interview Hub</h1>
      </header>
      <Route path={LOGIN_PAGE_PATH}>
        <ApplicantLogin/></Route>
      <Route path={CONFIRM_PAGE_PATH}>
        <ConfirmExam/></Route>
      <Route path={QUESTIONS_PAGE_PATH}>
        QUESTIONS HERE</Route>
    </div> 
  );
}

export default App;
