import { useSelector, useDispatch } from 'react-redux';
import { changeLoginInput, CONFIRM_PAGE_PATH, loadPath } from '../store';
import { useEffect } from 'react';

function ApplicantLogin()
{

    const dispatch = useDispatch();
    const loginInput = useSelector((state) =>
    {
        return state.login.loginInput;
    })
    const handleLoginInputChange = (event)=>
    {
        dispatch(changeLoginInput(event.target.value));
    }
    const handleSubmit = (event) =>
    {
        event.preventDefault();

        // TODO -> If login successfull, load user data and trigger next page
        dispatch(loadPath(CONFIRM_PAGE_PATH));
    }

    useEffect(() =>
        {
            dispatch(changeLoginInput(""));
    },[])

    return <div className="centerForm">
        <div className="centerLogin" style={{display: `inline-block`}}>
        <form onSubmit={handleSubmit} className="car-form panel">
            <div className='field-group'>
                <div className='field'>
                    <label className='label'>Email: </label>
                    <input 
                        className="input is-xpanded"
                        value={loginInput}
                        onChange={handleLoginInputChange}
                    ></input>
                </div>
            </div>
            <div className="field" style={{paddingTop: 10}}>
                <button style={{fontSize : `16px`}}>Login</button>
            </div>
        </form>
        </div>
    </div>
}

export default ApplicantLogin;
