import { combineReducers, createSlice } from '@reduxjs/toolkit';

const loginSlice = createSlice({
    name: 'login',
    initialState: 
    {
        loginInput: ''
    },
    reducers:
    {
        changeLoginInput(state, action)
        {
            state.loginInput = action.payload;
        }
    }
});

export const { changeLoginInput } = navigationSlice.actions;
export const navigationReducer = loginSlice.reducer;
