import { createSlice } from '@reduxjs/toolkit';

const userDataSlice = createSlice({
    name: 'userData',
    initialState:{
        id: 0,
        email: "",
        questionsId: [],
        confirmedMail : false,
        submitedExam: false,
        startedExam : false,
        examStartTime : 0
    },
    reducers:
    {
        changeUser(state, action)
        {
            state.id = action.payload.id;
            state.email = action.payload.email;
            state.questionsId = action.payload.questionsId;
            state.confirmedMail = action.payload.confirmedMail;
            state.submitedExam = action.payload.submitedExam;
            state.startedExam = action.payload.startedExam;
            state.examStartTime = action.payload.examStartTime;
        },
        setState(state, action)
        {
            if(state.key == "confirmedMail")
                state.confirmedMail = action.payload;
            else if(state.key == "submitedExam")
                state.submitedExam = action.payload;
            else if(state.key == "startedExam")
                state.startedExam = action.payload;
            else if(state.key == "examStartTime")
                state.examStartTime = action.payload;
        }
    }
});

export const { changeUser, setUserState } = userDataSlice.actions;
export const userReducer = userDataSlice.reducer;
