import { createSlice } from '@reduxjs/toolkit';
import { upload } from '@testing-library/user-event/dist/upload';

const userDataSlice = createSlice({
    name: 'userData',
    initialState:{
        id: 0,
        email: "",
        questionsId: [],
        confirmedMail : false,
        submitedExam: false,
        startedExam : false,
        examStartTime : ""
    },
    reducers:
    {
        changeUser(state, action)
        {
            state.id = action.payload.id;
            state.email = action.payload.email;
            state.questionsId = action.payload.questionsId;
            state.confirmedMail = action.payload.confirmedMail;
            state.submitedExam = action.payload.submitedExam;
            state.startedExam = action.payload.startedExam;
            state.examStartTime = action.payload.examStartTime;
        },
        setUserField(state, action)
        {
            action.payload.map((field)=>
            {
                switch(field.key)
                {
                    case "confirmedMail":
                        state.confirmedMail = field.value;
                        break;
                    case "submitedExam":
                        state.submitedExam = field.value;
                        break;
                    case "startedExam":
                        state.startedExam = field.value;
                        break;
                    case "examStartTime":
                        state.examStartTime = field.value;
                        break;
                }
            })
        },
    }
});

export const { changeUser, setUserField } = userDataSlice.actions;
export const userReducer = userDataSlice.reducer;
