import Link from '../Components/Link';
import { QUESTIONS_PAGE_PATH, SUBMIT_PAGE_PATH } from '../store';
import { useSelector } from 'react-redux';
import { usePostSubmissionMutation, useUpdateUserMutation, setUserState } from '../store';

function SubmitExam()
{
    const [submmitExam, submitResults] = usePostSubmissionMutation();
    const [updateUser, updateUserResults] = useUpdateUserMutation();

    const examPeriodLeft = 90;
    const submitDetails = `You still have a time period of ${examPeriodLeft} minutes to complete the exam. Are you sure you want to submmit your answers ? \n Please verify all of them, after this exam submmision you won't be able change or restart the exam.` ;

    const submitContent = "After submission we will review you answers and get in touch with feeback.";

    const userQuestions = useSelector((state)=> state.questionsData);
    const user = useSelector((state)=> state.userData);

    const handleConfirmSubmit = () =>{

        // TODO => load 'submission complete' page

        // Post submit questions at database 
        let answers = [];
        userQuestions.data.map((question) =>
        {
            answers.push({id: question.id, answerId: question.userAnswerId});
        })
        const submission = {userId: user.id, answers: answers};
        submmitExam(submission);

        // TODO => Post user current state
        setUserState({key:"submitedExam", value: true});
        updateUser(user);
    }

    return <div style={ { padding: 15, paddingLeft: 20 }}>
        <h1 style={{fontSize: 23}}>Confirm exam details :</h1>
        <div style={{ paddingTop: 10}}>
            <div style={{ border: '2px solid color:#282c3451', borderRadius: 20, padding: 5, backgroundColor: '#282c3451' }}>
                <h1 style={{padding: 10, fontSize: 15}}>
                    {submitDetails}
                </h1>
                <h1 style={{ padding: 10, fontSize: 15}}>
                    {submitContent}
                </h1>
                <h1 style={{padding : 10}}>{`Best of luck! HR Team`}</h1>
            </div>
            <div style={{paddingTop: 15, 
                display: "flex", 
                justifyContent: "center", 
                }}>
                <Link to={QUESTIONS_PAGE_PATH}>
                    <button style={{fontSize : `16px`}}>RETURN TO QUESTIONS</button>
                </Link>
                <Link to={SUBMIT_PAGE_PATH}>
                    <button onClick={handleConfirmSubmit} style={{fontSize : `16px`}}>CONFIRM SUBMIT</button>
                </Link>
            </div>
        </div>
    </div>
}

export default SubmitExam;
