import { createSlice } from '@reduxjs/toolkit';

const questionsSlice = createSlice({
    name: 'questionsData',
    initialState:{
        isDirty: true,
        data: [
        {
            id: 0,
            question: "",
            alternatives: [{id: 1, answer: "default"}], 
            answerId: 0,
            userAnswerId: 0
        }]
    },
    reducers:
    {
        loadQuestionsData(state, action)
        {
            state.data = action.payload;
        },
        selectQuestionAlternative(state, action)
        {
            state.isDirty = true;
            state.data.map((qObject) => {
                if(qObject.id == action.payload.questionId)
                {
                    qObject.userAnswerId = action.payload.alt;
                }
            })
        },
        setSubmissionDirty(state, action)
        {
            state.isDirty = action.payload.value;
        }
    }
});

export const { loadQuestionsData, selectAlternative, setSubmissionDirty } = questionsSlice.actions;
export const questionsReducer = questionsSlice.reducer;
