import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { changeUser } from "../store";
import { 
    useFetchQuestionsQuery, 
    useFetchSubmissionQuery,
    useFetchUserQuery,
    setupSubmission,
    loadQuestionsData
} from "../store";
import useSetUserField from "./useSetUserField";

function useLoadUser(loginInput)
{
    const [userLogged, setUserLogged] = useState(false);

    const user = useSelector(state=>state.userData);
    const dispatch = useDispatch();
    const fetchUser = useFetchUserQuery(loginInput);
    const fetchSubmission = useFetchSubmissionQuery(user);
    const fetchQuestions = useFetchQuestionsQuery(user);

    const [loginError, setLoginError] = useState(false);

    const setUserField = useSetUserField();

    // Dispatch loaded submission
    useEffect(()=>{
        console.log(fetchSubmission);
        if(!fetchSubmission.data) return;
        if(!userLogged) return;

        // When user is created, so is the submission
        // so we don't need to check for anything
        dispatch(setupSubmission(fetchSubmission.data[0]));
        console.log('loaded submission to slice');

    }, [fetchSubmission]);

    // Filter and dispatch loaded user questions
    useEffect(() => {
        if(!fetchQuestions.data)return;

        let filteredQuestions = [];
        fetchQuestions.data.map((question) =>
        {
            user.questionsId.map((id) => {
                if(id == question.id) 
                {
                    filteredQuestions.push(question);
                }
            });
        })

        dispatch(loadQuestionsData(filteredQuestions));
        // console.log('loaded filtered questions to slice');

        if(!user.startedExam)
        {
            const newDate = new Date();
            const startTime = `${newDate.getDate()}/${newDate.getMonth()+1}/${newDate.getFullYear()}-${newDate.getHours()}:${newDate.getMinutes()}:${newDate.getSeconds()}`;

            setUserField("startedExam", true);
            setUserField("examStartTime", startTime);
        }

        return;
    }, [user, fetchQuestions]);    

    // Dispatch loaded user data
    useEffect(() => {
        if(!fetchUser.data ||
        fetchUser.isLoading || 
        fetchUser.error || 
        loginInput == '') return;

        if(fetchUser.data.length <= 0){
            setLoginError(true);
            return;
        }

        dispatch(changeUser(fetchUser.data[0]));
        console.log(fetchUser.data[0]);
        setUserLogged(true);

        return;
    });

    return [fetchUser.isLoading, loginError];
}

export default useLoadUser;
