import './App.css';
import ApplicantLogin from './Pages/ApplicantLogin';
import ConfirmExam from './Pages/ConfirmExam';
import Route from './Components/Route';
import QuestionsExam from './Pages/QuestionsExam';
import SubmitExam from './Pages/SubmitExam';
import AwaitExamResults from './Pages/AwaitExamResults';
import { useDispatch, useSelector } from 'react-redux';
import { loadPath, LOGIN_PAGE_PATH, CONFIRM_PAGE_PATH, QUESTIONS_PAGE_PATH, SUBMIT_PAGE_PATH, WAIT_PAGE_PATH, setUserField } from './store';
import { useEffect } from 'react';
import ExamTimer from './Components/ExamTimer';
import UserUploader from './Effects/userUploader';

function App() {

  const dispatch = useDispatch();
  const user = useSelector(state => state.userData);
  const userUploader = useUploadUser();
  const submissionUploader = useUploadSubmission();

  useEffect(() =>
  {
    if(user.id == 0){
      dispatch(loadPath(LOGIN_PAGE_PATH));
    }
  }, []);

  return (
    <div>
      <header className="App-header">
        <h1>Interview Hub</h1>
        {(user.id != 0 && user.startedExam && !user.submitedExam) ? <ExamTimer></ExamTimer> : <></>}
      </header>

      <div>
        <Route path={LOGIN_PAGE_PATH}>
          <ApplicantLogin/></Route>
        <Route path={CONFIRM_PAGE_PATH}>
          <ConfirmExam/></Route>
        <Route path={QUESTIONS_PAGE_PATH}>
          <QuestionsExam/></Route>
        <Route path={SUBMIT_PAGE_PATH}>
          <SubmitExam/>
        </Route>
        <Route path={WAIT_PAGE_PATH}>
          <AwaitExamResults/>
        </Route>
      </div>
    </div> 
  );
}

export default App;
