import { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { setSubmissionDirty, usePostSubmissionMutation, useUpdateSubmissionMutation } from '../store';

function useUploadSubmission()
{
    const user = useSelector(state => state.userData);
    const userQuestions = useSelector((state)=>state.questionsData);
    const [updateSubmission, updateResults] = useUpdateSubmissionMutation();
    const [createSubmission, createResults] = usePostSubmissionMutation();
    const dispatch = useDispatch();

    useEffect(()=>{
        if(user.id == 0) return;
        if(!user.startedExam) return;

        console.log(userQuestions);
        let answers = [];
        const submission = {userId: user.id, answers: answers};

        if(userQuestions.isDirty)
        {
            console.log("Created submission");
            createSubmission(submission);
        }
        else
        {
            userQuestions.data.map((question) =>
            {
                answers.push({id: question.id, answerId: question.userAnswerId || 0});
            })
            updateSubmission(submission);
            console.log("Updated submission after mutation");
        }

        dispatch(setSubmissionDirty({value: false}));

    }, [userQuestions]);
}

export default useUploadSubmission;
