import { useState } from "react";
import { useDispatch } from "react-redux";
import { selectAlternative } from "../store";
import { useEffect } from "react";

function Question({data})
{
    const [userAnswer, setUserAnswer] = useState({questionId: 0, alt: 0});
    const dispatch = useDispatch();

    // Makes sure the answer selected by user gets dispatched to submission slice, 
    // triggering the mutate-upload hook and updatating the databse (see more at app.js 'submissionUploader')
    useEffect(() => {
        if(userAnswer.alt == 0) return;
        dispatch(selectAlternative({questionId: data.id, alt: userAnswer.id}));
    }, [userAnswer]);

    // Updates the current selected answer with user onclick alternative input
    const handleSetUserAnswer = (alternative) =>
    {
        setUserAnswer(alternative);
    }

    // Displays the content of the alternatives and connects to 'handleSetUserAnswer'
    const alternativesContent = data.alternatives.map((alternative) =>
    {
        const selectionStyle = userAnswer.id == alternative.id ? { fontWeight: 'bold', fontSize: 20 } : { fontSize: 15}
        const content = <div 
            className="box"
            style={selectionStyle}
            onClick={() => handleSetUserAnswer(alternative)} 
            key={alternative.id}>{alternative.answer}
        </div>
        return content;
    });

    return <div className="box" >
        <div style={{paddingLeft: 15}}>{data.question}</div>
        <div style={{paddingTop: 10}}>
            {alternativesContent}
        </div>

    </div>
}

export default Question;
