import { useState } from "react";
import { useFetchQuestionsQuery } from "../store";

function useLoadUserQuestions(user)
{
    const fetchQuestions = useFetchQuestionsQuery();

    if(!user || user.id == 0) return;
    if(fetchQuestions.isFetching ||
        fetchQuestions.isLoading ||
        fetchQuestions.error) return;

    console.log(fetchQuestions);
    // Filter and dispatch loaded user questions
    let filteredQuestions = [];
    fetchQuestions.data.map((question) =>
    {
        user.questionsId.map((id) => {
            if(id == question.id) 
            {
                filteredQuestions.push(question);
            }
        });
    })

    dispatch(loadQuestionsData(filteredQuestions));
    // console.log('loaded filtered questions to slice');

    if(!user.startedExam)
    {
        const newDate = new Date();
        const startTime = `${newDate.getDate()}/${newDate.getMonth()+1}/${newDate.getFullYear()}-${newDate.getHours()}:${newDate.getMinutes()}:${newDate.getSeconds()}`;

        setUserField("startedExam", true);
        setUserField("examStartTime", startTime);
    }

    return [fetchQuestions.isLoading, fetchQuestions.error];
}

export default useLoadUserQuestions;
